type Call struct {
	Target       common.Address
	AllowFailure bool
	Value        *big.Int
	CallData     []byte
}

type Result struct {
	Success    bool
	ReturnData []byte
}
type CCIPMsgData struct {
	RouterAddr    common.Address
	ChainSelector uint64
	Msg           router.ClientEVM2AnyMessage
	Fee           *big.Int
}

func MultiCallCCIP(
	evmClient blockchain.EVMClient,
	address string,
	msgData []CCIPMsgData,
) (*types.Transaction, error) {
	contractAddress := common.HexToAddress(address)
	multiCallABI, err := abi.JSON(strings.NewReader(MultiCallABI))
	if err != nil {
		return nil, err
	}
	boundContract := bind.NewBoundContract(contractAddress, multiCallABI, evmClient.Backend(), evmClient.Backend(), evmClient.Backend())
	routerABI, err := abi.JSON(strings.NewReader(router.RouterABI))
	if err != nil {
		return nil, err
	}
	ccipSend := routerABI.Methods["ccipSend"]
	sendID := ccipSend.ID
	var callData []Call
	for _, msg := range msgData {
		inputs, err := ccipSend.Inputs.Pack(
			msg.ChainSelector,
			msg.Msg,
		)
		if err != nil {
			return nil, err
		}
		inputs = append(sendID[:], inputs[:]...)
		data := Call{Target: msg.RouterAddr, AllowFailure: false, Value: msg.Fee, CallData: inputs}
		callData = append(callData, data)
	}
	opts, err := evmClient.TransactionOpts(evmClient.GetDefaultWallet())
	if err != nil {
		return nil, err
	}
	return boundContract.Transact(opts, "aggregate3Value", callData)
}
